namespace Zend\Log\Filter;

/**
 * @category   Zend
 * @package    Zend_Log
 */
interface FilterInterface
{
    /**
     * Returns TRUE to accept the message, FALSE to block it.
     *
     * @param array $event event data
     * @return boolean accepted?
     */
    public function filter(array $event);
}
